"use strict";let localforage=require('localforage');function exists(filename,callback){localforage.getItem(filename,function(err,value){if(value!==null){return callback(true);}else{return callback(false);}});}function rename(filename,newFilename,callback){localforage.getItem(filename,function(err,value){if(value===null){localforage.removeItem(newFilename,function(){return callback();});}else{localforage.setItem(newFilename,value,function(){localforage.removeItem(filename,function(){return callback();});});}});}function writeFile(filename,contents,options,callback){if(typeof options==='function'){callback=options;}localforage.setItem(filename,contents,function(){return callback();});}function appendFile(filename,toAppend,options,callback){if(typeof options==='function'){callback=options;}localforage.getItem(filename,function(err,contents){contents=contents||'';contents+=toAppend;localforage.setItem(filename,contents,function(){return callback();});});}function readFile(filename,options,callback){if(typeof options==='function'){callback=options;}localforage.getItem(filename,function(err,contents){return callback(null,contents||'');});}function unlink(filename,callback){localforage.removeItem(filename,function(){return callback();});}function mkdirp(dir,callback){return callback();}function ensureDatafileIntegrity(filename,callback){return callback(null);}module.exports.exists=exists;module.exports.rename=rename;module.exports.writeFile=writeFile;module.exports.crashSafeWriteFile=writeFile;module.exports.appendFile=appendFile;module.exports.readFile=readFile;module.exports.unlink=unlink;module.exports.mkdirp=mkdirp;module.exports.ensureDatafileIntegrity=ensureDatafileIntegrity;